import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";

import proj1 from "../../assets/img/beachfinder.png";
import proj2 from "../../assets/img/makersbnb.png";
import proj3 from "../../assets/img/acebook.png";
import proj4 from "../../assets/img/flutter.png";
import proj5 from "../../assets/img/portfolio.png";
import proj6 from "../../assets/img/travelJourney.png";
import proj7 from "../../assets/img/movieReview.png";
import proj8 from "../../assets/img/getQuotes.png";
import colorSharp2 from "../../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';


export const Projects = () => {

  const projects = [
    {
      title: "Beach Finder",
      description: "Full Stack",
      imgUrl: proj1,
      code: 'https://github.com/Hafizalimi7/beach-life'
    },
    {
      title: "Maker BnB",
      description: "Full Stack",
      imgUrl: proj2,
      code: 'https://github.com/Hafizalimi7/makers_bnb'
    },
    {
      title: "AceBook",
      description: "Full Stack",
      imgUrl: proj3,
      code: 'https://github.com/Hafizalimi7/acebook-node-priceless'
    },
    {
      title: "Portfolio",
      description: "Full Stack",
      imgUrl: proj5,
      code: 'https://github.com/Hafizalimi7/Portfolio'
    },
    {
      title: "Spotlas App",
      description: "Mobile Development",
      imgUrl: proj4,
      code: 'https://github.com/Hafizalimi7/SpotlasApp'
    },
  ];

  const projects2 = [
    {
      title: "Travel Journal",
      description: "Frontend",
      imgUrl: proj6,
      code: ''
    },
    {
      title: "Movie Review",
      description: "Full Stack",
      imgUrl: proj7,
      code: ''
    },
    {
      title: "Get Quotes",
      description: "Full Stack",
      imgUrl: proj8,
      code: ''
    },
  ];

  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                {/* <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p> */}
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">Tab 1</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Tab 2</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="third">Tab 3</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {

                          projects.map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />    
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                    <Row>
                        {
                          projects2.map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />
                            )
                          })
                        }
                      </Row>
                      <p>For more pro</p>
                    </Tab.Pane>
                    <Tab.Pane eventKey="third">
                      <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque quam, quod neque provident velit, rem explicabo excepturi id illo molestiae blanditiis, eligendi dicta officiis asperiores delectus quasi inventore debitis quo.</p>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
